from .models import Book, Authorfrom rest_framework import serializers# Serializers define the API representation.class AuthorSerializer(serializers.ModelSerializer):    class Meta:        ordering = ['-id']        model = Author        fields = ("id", "name", "biography", "date_of_birth", "books")        extra_kwargs = {'books': {'required': False}}class BookSerializer(serializers.ModelSerializer):    class Meta:        model = Book        fields = ("id", "title", "description", "publisher", "release_date", "authors")        extra_kwargs = {'authors': {'required': False}}